/**
 * Module dependencies.
 */
var util = require('util')
  , OAuth2Strategy = require('passport-oauth').OAuth2Strategy
  , InternalOAuthError = require('passport-oauth').InternalOAuthError;


/**
 * `Strategy` constructor.
 *
 * The Yixin authentication strategy authenticates requests by delegating to
 * Yixin using the OAuth 2.0 protocol.
 *
 * Applications must supply a `verify` callback which accepts an `accessToken`,
 * `refreshToken` and service-specific `profile`, and then calls the `done`
 * callback supplying a `user`, which should be set to `false` if the
 * credentials are not valid.  If an exception occured, `err` should be set.
 *
 * Options:
 *   - `appId`      your Yixin application's id
 *   - `appSecret`  your Yixin application's Secret
 *   - `callbackURL`   URL to which Yixin will redirect the user after granting authorization
 *   - `scope`         array of permission scopes to request.  valid scopes include:
 *                     'user', 'public_repo', 'repo', 'gist', or none.
 *
 * Examples:
 *
 *     passport.use(new YixinStrategy({
 *         appId: '123-456-789',
 *         appSecret: 'shhh-its-a-secret'
 *         callbackURL: 'https://www.example.net/auth/yixin/callback'
 *       },
 *       function(accessToken, refreshToken, profile, done) {
 *         User.findOrCreate(..., function (err, user) {
 *           done(err, user);
 *         });
 *       }
 *     ));
 *
 * @param {Object} options
 * @param {Function} verify
 * @api public
 */
function Strategy(options, verify) {
  options = options || {};
  options.authorizationURL = options.authorizationURL || 'http://open.plus.yixin.im/connect/oauth2/authorize';
  options.tokenURL = options.tokenURL || 'https://api.yixin.im/sns/oauth2/access_token';
  options.scopeSeparator = options.scopeSeparator || ',';

  OAuth2Strategy.call(this, options, verify);
  this.name = 'yixin';
  this._userProfileURL = options.userProfileURL || 'https://api.yixin.im/sns/userinfo';
  this._oauth2._useAuthorizationHeaderForGET = true;
}

/**
 * Inherit from `OAuth2Strategy`.
 */
util.inherits(Strategy, OAuth2Strategy);


/**
 * Retrieve user profile from Yixin.
 *
 * This function constructs a normalized profile, with the following properties:
 *
 *   - `provider`         always set to `yixin`
 *   - `userid`               the user's yixin  openid
 *   - `nickname`         the user's yixin nickname
 *   - `sex`              the gender of user
 *   - `city`             the city where user is living
 *   - `language`
 *
 * @param {String} accessToken
 * @param {Function} done
 * @api protected
 */
Strategy.prototype.userProfile = function(accessToken, done) {
  this._oauth2.get(this._userProfileURL, accessToken, function (err, body, res) {
    if (err) { return done(new InternalOAuthError('failed to fetch user profile', err)); }

    try {
      var json = JSON.parse(body);

      var profile = { provider: 'yixin' };
      profile.id = json.id;
      profile.displayName = json.name;
      profile.username = json.uid;
      profile.avatar = json.avatar;
      profile.profileUrl = json.alt;

      profile._raw = body;
      profile._json = json;

      done(null, profile);
    } catch(e) {
      done(e);
    }
  });
}


/**
 * Expose `Strategy`.
 */
module.exports = Strategy;