var crypto = require('crypto');
var querystring = require('querystring');
exports.urlTail = function(token) {
  var query = {
    timestamp: new Date().getTime(),
    nonce: parseInt((Math.random() * 100000000000), 10),
    echostr: 'hei'
  };
  var plaintext = [query.timestamp, query.nonce, token].sort().join('');
  query.signature = crypto.createHash('sha1').update(plaintext).digest('hex');
  return querystring.stringify(query);

};

